namespace ra\admin\controllers;

use Imagine\Image\Point;
use ra\admin\helpers\Image;
use Yii;
use yii\helpers\FileHelper;
use yii\web\Response;

class ImageController extends Controller
{
    public $quality = 90;
    public $dir = 'image';
    public $default = '';

    public function actionIndex($name, $type)
    {
        $dir = Yii::getAlias("@webroot/{$this->dir}/_{$type}/");
        if (file_exists($dir . $name))
            $this->printFile($dir . $name);

        FileHelper::createDirectory($dir);

        $crop = 1;
        if (strpos($type, 'x') !== false) {
            list($width, $height) = explode('x', trim($type, '!^'));
            if (!$width || !$height || strpos($type, '^')) $crop = 0;
            elseif (strpos($type, '!')) $crop = 2;
        } else {
            $width = $height = $type;
            $crop = 0;
        }

        $fromDir = Yii::getAlias("@webroot/{$this->dir}/tmp/");
        $from = $fromDir . $name;
        if (!file_exists($from)) {
            $name = 'default.jpg';
            $fromDir = Yii::getAlias("@runtime/");
            file_put_contents($fromDir . $name, base64_decode($this->default));
        }

        $thumb = Image::thumbnail(Yii::getAlias($fromDir . $name), $width, $height, $crop);

        $thumb->save($dir . $name, [
            'quality' => $this->quality,
            'png_compression_level' => 9,
        ]);

        $this->printFile($dir . $name);
    }

    public function printFile($file)
    {
        Yii::$app->response->format = Response::FORMAT_RAW;
        header("Expires: " . gmdate('r', time() + 60 * 60 * 24 * 30));
        header("Last-Modified: " . gmdate('r'));
        header('Content-Type: ' . FileHelper::getMimeTypeByExtension($file, FileHelper::$mimeMagicFile));
        header("Content-Length: " . filesize($file));
        readfile($file);
    }
}